$(document).ready(function(){$(".image-container").on("click",".deletebutton",function(e){var n=this.id,o=e.target,l=$('<div class="cp-spinner cp-skeleton"></div><p>Removing Image</p>');$.ajax({type:"POST",url:"/bolt/betterthumbs/files/delete",data:{img:n},beforeSend:function(){$(o).siblings(".image-to-delete").hide(),$(o).hide(),$(o).parent().append(l)},success:function(){$(o).hide(),$(o).siblings(l).hide(),$(o).before('<p class="removed-success">'+n+" Has been removed from the cache</p>")},error:function(e,n,o){console.log(e),console.log("Details: "+n+"\nError:"+o)}})}),$(".delete-all").on("click",function(e){var n=$(".delete-all").data("all"),o=$('<div class="cp-spinner cp-skeleton"></div><p>Removing All Images</p>'),l=e.target,s=$("#image-grid");$.ajax({type:"POST",url:"/bolt/betterthumbs/files/delete/all",data:{all:n},beforeSend:function(){$(l).parent().append(o),$(l).hide()},success:function(){$(l).hide(),$(l).siblings(o).hide(),$(l).before('<p class="removed-success">All Images Have Been Removed From The Cache</p>'),s.empty(),s.append('<div class="column"><p>No Images In Cache</p></div>')},error:function(e,n,o){console.log(e),console.log("Details: "+n+"\nError:"+o),$(l).parent().append(e.responseText)}})})});